import React, { useState } from "react";
import './style.css'
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.min.css';
import emailjs from 'emailjs-com';
import { useRef } from 'react';


export default function Complaint() {
    // for emailjs
        const form = useRef();
        const sendEmail = (e) => {
            e.preventDefault();
            emailjs.sendForm ('service_dimo38h', 'template_do7sveg', form.current, 'YOUR_PUBLIC_KEY').then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
            
    // Modals
    const [showModal, setShowModal] = useState(false);
            const handleClose = () => {
                setShowModal(false);
            }
            const handleShow = () => {
                setShowModal(true);
            }
            return (
                <>
                    <button className="complaint" onClick={handleShow}>Complaint <i class="fa-solid fa-clipboard-question"></i></button>
                    <Modal show={showModal} onHide={handleClose} className="complaint">
                        <form ref={form} onSubmit={sendEmail}>
                            <label>Name</label>
                            <input type="text" name="user_name"></input>
                            <label>Email</label>
                            <input type="email" name="user_email"></input>
                            <label>Message</label>
                            <textarea name="message" />
                            <input type="submit" value="Send" />
                        </form>

                    </Modal>
                </>
            )


        }

    }
