import React, { useRef, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './login.scss';
import { ToastContainer, toast } from 'react-toastify';

function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const formref = useRef()

  const validationReg = async (e) => {
    e.preventDefault();
    const isValid = /@jmangroup\.com$/.test(email);
    if (!isValid) {
      toast.error("Enter the organisation mail")
      formref.current.reset()
    }
    else {
      if (email.trim() !== '' && password.trim() !== '') {
        try {
          const response = await axios.post('http://localhost:5000/users/', {
            email,
            password,
          });
          console.log("response from backend", response.data);
          if (response.data === 'User logged') {
            toast.success("Login successful")
            setTimeout(() => {
              navigate('/userform')
            }, 3000)
          }
          else if (response.data === 'Admin logged.') {
            toast.success(" Admin Login successful")
            setTimeout(() => {
              navigate('/admin_training')
            }, 3000)
          }
        }

        catch (error) {
          console.error('Sign-in error:', error);
        }

      }
    }
  }

  return (
    <div className="login-container">
      <div className="login-left">
        <ToastContainer />
        <img src="/login/login3.png" alt="Login" />
      </div>
      <div className="login-right">
        <h1 className="h1">Welcome Back :)</h1>
        <form onSubmit={validationReg} ref={formref}>
          <div className="login-input-container">
            <i className="fa-regular fa-envelope"></i>
            <input
              type="email"
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              required
            />
          </div>
          <div className="login-input-container">
            <i className="fa-solid fa-lock"></i>
            <input
              type="password"
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              required
            />
          </div>
          <div className="buttons">
            <Link to="/signup">
              <button className="oldloginbutton">New User?</button>
            </Link>
            <button className="loginbutton" type="submit">
              Login
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
