import React, { useState } from "react";
import './style.css'
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.min.css';
import emailjs from 'emailjs-com';
import { useRef } from 'react';



export default function Complaint() {
    const form = useRef();
    const [showModal, setShowModal] = useState(false);

    const handleClose = () => {
        setShowModal(false);
    }

    const handleShow = () => {
        setShowModal(true);
    }

    const sendEmail = (e) => {
        e.preventDefault();
        emailjs.sendForm('service_dimo38h', 'template_do7sveg', form.current, '9rndwk3q5_ec5AzuH').then(
            (result) => {
                console.log(result.text);
            },
            (error) => {
                console.log(error.text);
            }
        );
    }

    return (
        <>
            <button className="complaint" onClick={handleShow}>
                Complaint <i className="fa-solid fa-clipboard-question"></i>
            </button>
            <Modal show={showModal} onHide={handleClose} className="complaint">
                <form ref={form} onSubmit={sendEmail}>
                    <label>Name</label>
                    <input type="text" name="user_name" />
                    <label>Email</label>
                    <input type="email" name="user_email" />
                    <label>Message</label>
                    <textarea name="message" />
                    <input type="submit" value="Send" />
                </form>
            </Modal>
        </>
    );
}
